def open_file(entry):
    win=Tk()
    win.withdraw()

    up_file_path = filedialog.askopenfilename()
    if entry==0:
        file_name1=up_file_path
        canvas.itemconfig(file_name1_txt, text=file_name1)
    elif entry==1:
        file_name2=up_file_path
        canvas.itemconfig(file_name2_txt, text=file_name2)

def complete():
    hr=entry_1.get()
    mn=entry_2.get()

    try:
        true_file1_name=(file_name1.split("/"))[-1]
    except:
        true_file1_name='NONE'
    try:
        true_file2_name=(file_name2.split("/"))[-1]
    except:
        true_file2_name='NONE'

    data0={}
    with open("Files/Data/Penalty_Info.json", "w") as pen_info_file:
        data0["Penalty Info"]=[true_file1_name,true_file2_name]
        data0["Penalty Time"]=f"{hr}:{mn}"
        json.dump(pen_info_file, data0, indent=4)

    with open("Files/Data/First_open.csv", 'w', newline='') as first_open_check_file:
        fw=csv.writer(first_open_check_file)
        fw.writerow(["True"])

    subprocess.Popen(['python', 'gui.py'])
    window.quit()

